-- Find the opportunity ID for Weebs pepppes
SELECT id, name FROM opportunities WHERE name LIKE '%weebs%' OR name LIKE '%pepppes%' LIMIT 1;

-- Update call logs for lead 120375 to point to the opportunity
UPDATE callLogs 
SET opportunityId = (SELECT id FROM opportunities WHERE name LIKE '%weebs%' LIMIT 1), 
    leadId = NULL 
WHERE leadId = 120375;

-- Update notes for lead 120375 to point to the opportunity
UPDATE notes 
SET opportunityId = (SELECT id FROM opportunities WHERE name LIKE '%weebs%' LIMIT 1), 
    leadId = NULL 
WHERE leadId = 120375;

-- Delete the lead
DELETE FROM leads WHERE id = 120375;

-- Verify the migration worked
SELECT id, leadId, opportunityId, callOutcome FROM callLogs WHERE opportunityId = (SELECT id FROM opportunities WHERE name LIKE '%weebs%' LIMIT 1);
SELECT id, leadId, opportunityId, LEFT(content, 50) as content_preview FROM notes WHERE opportunityId = (SELECT id FROM opportunities WHERE name LIKE '%weebs%' LIMIT 1);

