-- Create test lead
INSERT INTO leads (companyName, contactName, phone, email, stage, ownerId, createdAt, updatedAt)
VALUES ('Test Conversion Co', 'John Tester', '555-0123', 'john@test.com', 'qualified', 1, NOW(), NOW());

SET @lead_id = LAST_INSERT_ID();

-- Add call log
INSERT INTO callLogs (leadId, callOutcome, callDate, callDuration, notes, ownerId, createdAt, updatedAt)
VALUES (@lead_id, 'Connected', NOW(), 120, 'Great conversation about their needs', 1, NOW(), NOW());

-- Add note
INSERT INTO notes (leadId, content, ownerId, createdAt, updatedAt)
VALUES (@lead_id, 'Follow up next week about pricing', 1, NOW(), NOW());

-- Return the test lead ID
SELECT @lead_id as test_lead_id, 
       (SELECT COUNT(*) FROM callLogs WHERE leadId = @lead_id) as call_logs_count,
       (SELECT COUNT(*) FROM notes WHERE leadId = @lead_id) as notes_count;

